function[]=ca_norm_mat();%Normierung fuer Matlab Kohaerenzfiles, d.h. nicht "ASCII" filescd Voyager:users:Gruber:coh_gestalt:bslvec = [650+2 : 750]bslvec = [1150+2 : 1250]files = ['gm_r4___1.coh';'gm_r4_1.coh.s';'gm_r4___2.coh';'gm_r4_2.coh.s';'gm_r4___3.coh';'gm_r4_3.coh.s';'gm_r4___4.coh';'gm_r4_4.coh.s';         'gm_r4___5.coh';'gm_r4_5.coh.s';'gm_r4___6.coh';'gm_r4_6.coh.s';'gm_r4___7.coh';'gm_r4_7.coh.s';'gm_r4___8.coh';'gm_r4_8.coh.s'];for lv = 1 : size(files,1)  x = [];  eval(['load ' files(lv,:) ' -mat'])  x=ResMat_s;ResMat_s=[];  size(x)  for compar = 1 : 171    x(compar, [3:length(x(compar, :))]) = (x(compar, [3:length(x(compar, :))]) - mean(x(compar, bslvec))) ./ std(x(compar, bslvec));  end;    eval(['save ' files(lv,:) '.n2 x -mat'])end;