% extract_coh_letfunction [cohermat] = extract_coh_let(infilepath)% 1. regionsDL = [21 20 25 24 19];BL = [35 28 34 39 33];WL = [46 47 48 42 50 ];PL = [52 53 60];OL = [58 64 65 70 71];DR = [119 124 3 4 10];BR = [121 122 123 1 117];WR = [102 103 104 109 99 ];PR = [86 87 93];OR = [96 97 90 91 84];regionvec = ['DL'; 'BL'; 'WL'; 'PL'; 'OL'; 'DR'; 'BR'; 'WR'; 'PR'; 'OR']% 2. read matrixactmat = load(infilepath); % 3. reshape matrix% not that first column is zeros such that vollmat(x,1) = 0vollmat   = [actmat; zeros(1,129)] + [ actmat' zeros(129, 1)];cohermat = zeros(10,10);for region1_index = 1:10	for region2_index = 1:10				region1 = eval(regionvec(region1_index, :));		region2 = eval(regionvec(region2_index, :));				dummysum = 0;				for reg1_elecindex = 1:length(region1)			for reg2_elecindex = 1:length(region2)								dummysum = dummysum+vollmat(region1(reg1_elecindex), region2(reg2_elecindex));											end		end		cohermat(region1_index, region2_index) = dummysum ./ (reg1_elecindex*reg2_elecindex)	endend			