% wa_coh.mfunction [avg_valor_mat] = wa_coh(dirname);		f0_start = 83;		f0_end = 83;		delta_f0 = 1;wavelet = gener_Wav(4096, delta_f0, f0_start, f0_end);filemat = getfilesindir(dirname)for file = 1: size(filemat, 1);			matrix = read_avr(filemat(file, :));		data1 =  matrix(41,:);		data_pad1 = [data1'; zeros(4096-3280,1)];		[Valorcomplejo_1] = wa_ca(data_pad1, wavelet, 4096, 3280, 100);				% vglkanaele				% kanal 2		data2 =  matrix(42,:);		data_pad2 = [data2'; zeros(4096-3280,1)];		[Valorcomplejo_2] = wa_ca(data_pad2, wavelet, 4096, 3280, 100);				% kanal 3		data3 =  matrix(43,:);		data_pad3 = [data3'; zeros(4096-3280,1)];		[Valorcomplejo_3] = wa_ca(data_pad3, wavelet, 4096, 3280, 100);				% kanal 4		data4 =  matrix(40,:);		data_pad4 = [data4'; zeros(4096-3280,1)];		[Valorcomplejo_4] = wa_ca(data_pad4, wavelet, 4096, 3280, 100);				% kanal 5		data5 =  matrix(39,:);		data_pad5 = [data5'; zeros(4096-3280,1)];		[Valorcomplejo_5] = wa_ca(data_pad5, wavelet, 4096, 3280, 100);				% kanal 6		data6 =  matrix(38,:);		data_pad6 = [data6'; zeros(4096-3280,1)];		[Valorcomplejo_6] = wa_ca(data_pad6, wavelet, 4096, 3280, 100);								valor_mat = [Valorcomplejo_1; Valorcomplejo_2; Valorcomplejo_3; Valorcomplejo_4; Valorcomplejo_5; Valorcomplejo_6];						avg_valor_mat = sum(valor_mat)./6;										eval(['save ' deblank(filemat(file, :)) '.coh   avg_valor_mat  -ascii']) 						end									