%==================================================================%==================================================================clear all;%%	BatchMEG.m%%	Markus Junghoefer	[2000]%%%	Function definition%	%%%%%=================================================================%==================================================================[DefFilePath]=SetDefPath('r','*.msi')[NMsiFiles,MsiFileMat,NonUse,BatchFilePath]=ReadFileNames([],DefFilePath,'Choose MSI file(s):');if NMsiFiles>0	SetDefPath('w',BatchFilePath);endfor FileIndex=1:NMsiFiles	[MsiFile,MsiPath,MsiFilePath]=GetFileNameOfMat(MsiFileMat,FileIndex);% 	[MsiFilePath,SetFilePath,SesFilePath,AvgFilePath,SensorMat,ParaVec,SensorNameVec,SensorIndexVec]=...% 	TransMsiContSess(MsiFilePath,PreTrig,PostTrig,ReTrig,MinNTrigPoints,NStep,NDefTrig)	[NonUse,NonUse,MsiFilePath]=TransMsiContSess(MsiFilePath,136,677,1,600,[],36);endreturn% 	[AEMFilePathMat]=CalcAutoEditMEGMat(MsiFileMat,TimeStatusVec,CalcBaseStatus,BaseStartPoint,BaseEndPoint);	[AEMFilePathMat]=CalcAutoEditMEGMat(MsiFilePath,[],1,1,678);