% fourier2mn% transfoms 129 channel Fourier components (imag or real)% into source space, computes 3 dipole components% attention: only one vector allowed - imag or real!!!!!!function [] = fourier2mn(FourCompVec, G);if nargin < 2	[GFile GPath] = uigetfile('*', 'select Gfile')	GFilePath = [GPath GFile];	eval(['load ' GFilePath]);	G = eval([GFile(1:length(GFile)-4)]);endif nargin < 1	[File Path] = uigetfile( '*','select Fourcompfile')	fourFilePath = [Path File];	eval(['load ' fourFilePath]);	FourCompVec = eval(File);else fourFilePath = ['ubies:tmp:'];end	%open new files	%--------------------------------------	% open new file 1 for first component	%--------------------------------------	NewFileFid1 = fopen([fourFilePath '.MN1'], 'w')     		%--------------------------------------	% open new file 2 for second component	%--------------------------------------	NewFileFid2 = fopen([fourFilePath '.MN2'], 'w')     		%--------------------------------------	% open new file 3 for third component	%--------------------------------------	NewFileFid3 = fopen([fourFilePath '.MN3'], 'w')     			invsol = epoch_invmap(FourCompVec,G);   % Ÿbergeben an epoch invmap			fwrite(NewFileFid1, (invsol(1:3:387, :))' , 'int16');	fwrite(NewFileFid2, (invsol(2:3:387, :))' , 'int16');	fwrite(NewFileFid3, (invsol(3:3:387, :))' , 'int16');	fclose('all')