function [data, latencies, name, pathname] = read_mfxtxt(filename, format);if nargin==0,   disp(' [data, name, pathname] = read_mfxtxt(filename, format); ');   return;end;if isempty(filename),		% No name specified	[name, pathname] = uigetfile('*.asc', 'Choose a MFX-TXT file!');	filename = sprintf('%s%s', pathname, name);end;header_lines = 10;fid = fopen(filename, 'r');for i=1:header_lines,   line = fgetl(fid);end;line = fgetl(fid);i=1;while isstr(line),   disp(i);   data(:,i) = sscanf(line, '%f', inf);	line = fgetl(fid);	i = i+1;end;   [m n] = size(data);if strcmp(format, 'l'),   latencies = data(1,:);   data = data(2:m,:);else,   latencies = 1:m;end;fclose(fid);