function write_matrix(matrix, name, m, n); if nargin==1 | isempty(name),		% No name specified	[filename, pathname] = uigetfile('*.*', 'Choose in which file to store the matrix!');	name = sprintf('%s%s', pathname, filename);end;if nargin==2 | nargin==1,			% Only matrix given => Matrix assumed to be stored by rows	[m n] = size(matrix);end;	 						% Write matrix with given dimensionsfid = fopen(name, 'w');for i=1:m,	for j=1:n,		fprintf(fid, '%f ', matrix(i,j));	end;	fprintf(fid, '\n');end;fclose(fid);return;