% PCA2SAmat% reads specific spatial/temporal factor scores from scads-file% and arranges values according to ANOVAfactorial design% ready for Superanova formatfunction [] = PCA2SAmat(scoreindices);conditionvec= ['BSL';  'BSR'; 'DLL'; 'DRR'; 'SLL'; 'SRR']vps = ['HS97001_';'HS97003_';'HS98005_';'HS98009_';'HS98011_';'HS98013_';'NS98004_';'NS98006_';'NS98007_';'NS98008_';'NS98010_'; 'NS98012_']; % hypnopath = 'ElNino:AS_Exps:hypnosis_pain:4_PCA:tempPCAres(2nd_step):MN_subj_scores:'for cond = 1 : 6	for vpindex = 1 : size(vps,1)			vp = vps(vpindex,:);			file = [ vp conditionvec(cond, :) '.fl40.E1scores.at' ]				filepath = [path file]; 			[scoremat] = ReadAvgFile(filepath);				ind = sub2ind(size(scoremat),scoreindices(1), scoreindices(2) ); 			SAvec(vpindex) = scoremat(ind); 				end	if cond ==1		SAmat = SAvec';			else		SAmat = [SAmat SAvec'];	end	endeval(['save ' path 'SAMat_albed_' [num2str(scoreindices(1)) 'SF_' num2str(scoreindices(2)) 'TF'] '  SAmat   -ascii'])