% make_PCA_mat% reads SCADS at*.ar files in filemat % and produces one voltage matrix% !!!!!!! note: default is observations = electrodes ('spatial PCA') % ==> lines 17 and 19 : a'; for temporal PCA : afunction [PCAMat] = make_PCA_mat(filemat, bslvec, downsamp_fac);	for fileIndex = 1 : size(filemat, 1); 		a = ReadAvgFile(filemat(fileIndex, :));		fprintf('baseline correction for file number  %g', fileIndex)		a = bslcorr(a, bslvec);		a = a(:, 140:downsamp_fac:size(a, 2)-175);		size(a)		if fileIndex == 1;			PCAMat = a';		else			PCAMat = [PCAMat; a'];		end			end	