% PCA2SAmat% reads specific spatial/temporal factor scores from scads-file% and arranges values according to ANOVAfactorial design% ready for Superanova formatfunction [] = temphypno();%conditionvec = [ 'DLL'; 'DRR'; 'SLL'; 'SRR']conditionvec = [ 'bslr'; 'bsll'] vps = ['HS97001_';'HS97003_';'HS98005_';'HS98009_';'HS98011_';'HS98013_']% hypno% vps = [;'NS98004_';'NS98006_';'NS98007_';'NS98008_';'NS98010_'; 'NS98012_'];path = 'ElNino:AS_Exps:hypnosis_pain:ERPhypno:'for cond = 1 : size(conditionvec,1)	for vpindex = 1 : size(vps,1)			vp = vps(vpindex,:);			file = [ vp conditionvec(cond, :) '.fl40.E1.atg.ar' ]				filepath = [path file]; 			[einfilemat, File,Path,FilePath,NTrialAvgVec,StdChanTimeMat,...	SampRate,AvgRef,Version,MedMedRawVec,MedMedAvgVec,EegMegStatus,NChanExtra] = ReadAvgFile(filepath);				if vpindex == 1			summat = einfilemat;		else			summat = summat + einfilemat;		end						end	Grandmeanmat = summat ./  size(vps,1);		SaveAvgFile([path 'GM_' vp(1:2) '_' conditionvec(cond, :) '.fl40.E1.atg.ar'], Grandmeanmat,NTrialAvgVec,StdChanTimeMat,...	SampRate,MedMedRawVec,MedMedAvgVec,EegMegStatus,NChanExtra )		end