% circvar% computes circular variance for complex input vector invec % or columnwise for complex matrix inmatfunction [circvar] = circvar(inmat)absdevvec = [];meanvec = mean(inmat);for step = 1: size(inmat, 2)		if step == 1		absdevvec = abs(inmat(step,:)-meanvec);	else		absdevvec = absdevvec + abs(inmat(step,:)-meanvec);	end	endcircvar = absdevvec ./ size(inmat, 1);	