%LoopTsquare% bedient circTsquare% plottet circular T^2 fuer jede elec und jede vpvpvec = [1:19];summat = []for vp = 1:19	Fouriercompfilepath =(['ubies:emosteady:forTsquare:' num2str(vpvec(vp)) 'allbedT2.mat'])	[Tsquarevec] = circTsquare(Fouriercompfilepath, 'albedmat');	subplot(4,5,vp), plot(Tsquarevec)	summat(vp,:) = Tsquarevec;endsubplot(4,5,20), plot(mean(summat))