% rayleigh% computes rayleigh circular test for  two equal-size% complex column vectors (0r matrices of columnvectors)% representing two conditionsfunction [rayleighvec] = rayleigh(mat1, mat2)diffmat = mat1-mat2for index = 1: size(mat1,2);		diffmatnorm(:,index) = diffmat(:,index) ./ abs(diffmat(:,index));	sumcomplex(:,index) = sum(diffmatnorm(:,index));rayleighvec(index) = abs(sumcomplex(:,index)./size(mat1,1));end