%==================================================================%==================================================================clear all;%%	BatchOperant.m%%%%	Function definition%	%%%%%=================================================================%==================================================================% attention: 900 => 700 ; 2000 => 1500[N900Files,Egis900FileMat]=ReadFileNames([],'*EP.E*','Choose Egis EP file:');Egis2000FileMat=[];for FileIndex=1:N900Files	[File900,Path900,FilePath900]=GetFileNameOfMat(Egis900FileMat,FileIndex);	FilePathTmp1=FilePath900(1:length(FilePath900)-5);	ExtTmp=FilePath900(length(FilePath900)-2:length(FilePath900));	FilePathTmp2=[FilePathTmp1,'K',ExtTmp];	[Egis2000FileMat,N2000Files]=AddFileName2Mat(Egis2000FileMat,FilePathTmp2);	FilePathTmp2=[FilePathTmp1,'OK',ExtTmp];	[Egis2000FileMat,N2000Files]=AddFileName2Mat(Egis2000FileMat,FilePathTmp2);	FilePathTmp2=[FilePathTmp1,'SP',ExtTmp];	[Egis2000FileMat,N2000Files]=AddFileName2Mat(Egis2000FileMat,FilePathTmp2);endEgis900FileMat=Egis900FileMatEgis2000FileMat=Egis2000FileMat%===============CalcAutoEditMat ohne avg ref EEG und ohne flat====================================%[AEMFilePathMat]=CalcAutoEditMat(FileMat,TimeStatusVec,ch_CalcAvgRef,ch_UseSpecChan,...%ch_CalcBaseline,BaseStartPoint,BaseEndPoint,ch_ChooseTimeInd,LFlatWin,ch_CalcFlaMat,ch_UseAWE);[AEMFilePath900Mat]=CalcAutoEditMat(Egis900FileMat,ones(1,700),0,0,1,1,199,0,8,0,0);[AEMFilePath2000Mat]=CalcAutoEditMat(Egis2000FileMat,ones(1,1500),0,0,1,1,199,0,8,0,0);%======Suche nach schlechten Kanaelen ==================================%FindBadChan(AEMFilePathMat,AWELimesFac,BatchStatus);FindBadChan(AEMFilePath900Mat,3,1);FindBadChan(AEMFilePath2000Mat,3,1);%======Suche nach schlechten trials ==================================% [AWEFileMat]=FindBadChanTrial(FilePathMat,AWELimesFac,BatchStatus);FindBadChanTrial(AEMFilePath900Mat,2.5,1);FindBadChanTrial(AEMFilePath2000Mat,2.5,1);%===============CalcAutoEditMat mit avg ref EEG und mit flat====================================[AEMFilePath900Mat]=CalcAutoEditMat(Egis900FileMat,ones(1,700),1,1,1,1,199,0,8,1,1);[AEMFilePath2000Mat]=CalcAutoEditMat(Egis2000FileMat,ones(1,1500),1,1,1,1,199,0,8,1,1);