% resampleapp% resampled app files mit resamp.m%function () = resampapp(AppFilePath)[Data,AppVersion,LAppHeader,ScaleBins,NChan,NPoints,NTrials,SampRate,ch_AvgRef,File,Path,FilePath] = readappdata;NewFilePath = [FilePath 'resamp'];NewFid=fopen(NewFilePath,'w+');fwrite(NewFid,AppVersion,'int16');fwrite(NewFid,LAppHeader,'int16');fwrite(NewFid,ScaleBins,'int16');fwrite(NewFid,NChan,'int16');fwrite(NewFid,NPoints,'int16');fwrite(NewFid,NTrials,'int16');fwrite(NewFid,500,'int16');fwrite(NewFid,ch_AvgRef,'int16');		AppTell=ftell(NewFid);ZeroVec=zeros(LAppHeader-AppTell,1);fwrite(NewFid,ZeroVec,'int8');for trialInd = 1 : NTrials	[Data,Version,LHeader,ScaleBins,NChan,NPoints,NTrials,SampRate,ch_AvgRef,File,Path,FilePath] = readappdata(FilePath,trialInd);	datanew = resample(Data', 2,1);	trialMat = datanew(150:661,:);	SaveAppData(version,NewFid,FilePath,ScaleBins,NChan,NPoints,NTrials,trialInd,trialMat)endfclose all