function wcfreqmeanfocs = allfreqwcmeanfocs(animat, facmat, armmat, legmat, t1,t2)% compute frequency bandsdisp ('compute frequency bands.');wcfreqmeanfocs(1:24) = allwcmeanfocs(animat, facmat, armmat, legmat, 8, 12, t1, t2);wcfreqmeanfocs(25:48) = allwcmeanfocs(animat, facmat, armmat, legmat, 14, 26, t1, t2);wcfreqmeanfocs(49:72) = allwcmeanfocs(animat, facmat, armmat, legmat, 26, 36, t1, t2);wcfreqmeanfocs(73:96) = allwcmeanfocs(animat, facmat, armmat, legmat, 36, 46, t1, t2);wcfreqmeanfocs(97:120) = allwcmeanfocs(animat, facmat, armmat, legmat, 46, 56, t1, t2);wcfreqmeanfocs(121:144) = allwcmeanfocs(animat, facmat, armmat, legmat, 56, 66, t1, t2);wcfreqmeanfocs(145:168) = allwcmeanfocs(animat, facmat, armmat, legmat, 66, 76, t1, t2);