function allfocdata = allmeanfocs(inmat, f1,f2,t1,t2)% first compute mean over freq and timeframemeangridmat = meangridcell(inmat, f1, f2, t1, t2);% then comute mean over foci% left hemisphere central - lateralallfocdata(1) = focmeangridcell(meangridmat,[3,4,10,11,12,13]);allfocdata(2) = focmeangridcell(meangridmat,[23,24,25,41,42,43]);allfocdata(3) = focmeangridcell(meangridmat,[62,63,64,85,86,87]);%right hemi allfocdata(4) = focmeangridcell(meangridmat,[6,7,15,16,17,18]);allfocdata(5) = focmeangridcell(meangridmat,[32,33,34,52,53,54]);allfocdata(6) = focmeangridcell(meangridmat,[75,76,77,100,101,102]);