function append_son(flag);namelist = char('son1', 'son2', 'son3', 'son4', 'son5');nr_names = length(namelist(:,1));for i=1:nr_names,   for j=1:3,      if strcmp(flag, 'ori'),          namein = sprintf('c:\\data\\son\\%s_mnori%d.avr', deblank(namelist(i,:)), j);      else,         namein = sprintf('c:\\data\\son\\%s_mn%d.avr', deblank(namelist(i,:)), j);      end;   	data = read_avr(namein);	   data = append_nsum(data);	   if strcmp(flag, 'ori'),          nameout = sprintf('c:\\data\\son\\%s_mnori%da.avr', deblank(namelist(i,:)), j);      else,         nameout = sprintf('c:\\data\\son\\%s_mn%da.avr', deblank(namelist(i,:)), j);      end;      write_avr(data, nameout, -98.3, 1.966);   end;end;