function [locations, orientations, moments] = arrows_on_circle(fraction);if nargin==0,   disp(' [locations, orientations, moments] = arrows_on_circle(fraction); ');   return;end;count = 0;radius = 80;fraction = 0.4;for shell=1:4,   angle_step = fraction/(2*pi);   angle = 0;	while angle<2*pi,	   count = count+1;	   locations(1,count) = radius*cos(angle);	   locations(2,count) = radius*sin(angle);	   locations(3,count) = 0;	   orientations(1,2*(count-1)+1) = -sin(angle);	   orientations(2,2*(count-1)+1) = cos(angle);	   orientations(3,2*(count-1)+1) = 0;%   orientations(1,2*(count-1)+2) = 0;%   orientations(2,2*(count-1)+2) = 0;%   orientations(3,2*(count-1)+2) = 1;	   orientations(1,2*(count-1)+2) = cos(angle);	   orientations(2,2*(count-1)+2) = sin(angle);	   orientations(3,2*(count-1)+2) = 0;	   moments(count) = 1;      angle = angle+angle_step;   end;   radius = radius-20;   fraction = fraction+0.2;end;