function avg_cos = average_cot(filelist);if nargin==0,   disp( ' avg_cos = average_cot(filelist); ' );   return;end;if isempty(filelist),   filelist = read_filelist;end;nr_sets = length(filelist(:,1));disp(filelist(1,:));avg_cos = dlmread(filelist(1,:), ',');for i=2:nr_sets,   disp(filelist(i,:));	act_cos = dlmread(filelist(i,:), ',');   avg_cos = avg_cos + act_cos;end;avg_cos = avg_cos/nr_sets;