function avg_sensor = average_pmg(filelist);if nargin==0,   disp( ' avg_sensor = average_pmg(filelist); ' );   return;end;if isempty(filelist),   filelist = read_filelist;end;nr_sets = length(filelist(:,1));disp(filelist(1,:));avg_sensor = read_pmg(deblank(filelist(1,:)));for i=2:nr_sets,   disp(filelist(i,:));	sensor = read_pmg(deblank(filelist(i,:)));   avg_sensor = avg_sensor + sensor;end;avg_sensor = avg_sensor/nr_sets;