function [avg, namelist] = average_time(namelist, format, from, to, nameout);if nargin==0,   disp(' [avg] = average_time(namelist, format, from, to, nameout); ');   return;end;if isempty(namelist),   [namelist, names, paths] = read_filelist(format);end;[data, m, n, latencies] = read_data(namelist, '', 'avr');disp(latencies(1,from));disp(latencies(1,to));for i=1:length(data(1,1,:)),   avg(:,i) = mean(data(:,from:to,i)')';end;if ~isempty(nameout),   disp(nameout);   write_avr(avg, nameout, 1, 1);end;