function  data = baseline_correct_avr(name_in, name_out, base_from, base_to);if nargin==0,   disp(' data = baseline_correct_avr(name_in, name_out, base_from, base_to); ');   return;end;[data, latencies] = read_avr(name_in);[m, n] = size(data);%disp (latencies);[tmp, index_from] = min(abs(latencies-base_from));[tmp, index_to] = min(abs(latencies-base_to));%disp (index_from);%disp (index_to);mean_baseline = mean(data(:,index_from:index_to)');for i=1:m,   data(i,:) = data(i,:)-mean_baseline(i);end;if ~isempty(name_out),   write_avr(data, name_out, latencies(1), latencies(2)-latencies(1));end;