% bslcorrWAMat% subtracts the 2dimensional time-frequency matrix selected by the user% from all submatrices(timepints) of 3dim WAmatrixfunction[outWaMat] = bslcorrWAMat(inMat, timevec);if nargin < 2   timevec = input('give indices of the timevector to be used as baseline');endif nargin < 1   [File, Path] = uigetfile('*');   disp([Path File])   eval(['load ' Path File]);   inMat = AvgWaPower;endbslMat = squeeze(mean(inMat(:, :, timevec), 3));size(bslMat)for tpoint = 1 : length(inMat(1, 1, :));   outWaMat(:, :, tpoint) = squeeze(inMat(:, :, tpoint))-bslMat;end