% Read in matrix, compute correlation matrix of its rows, and plot it% Dimensions of matrix and range of elements for the columns to be included must be given% OH 22.10.97function [C, data] = correl_from_file(filename, m, n, index_from, index_to);data = read_matrix(filename, m, n);    % Own read-functionA = data(:, index_from:index_to);C = correl_mat(A, A);    % Own correlation functionsurf_matrix(C);    % Own plot-functiontitle(filename);clear A;