function diff_avr(name1, name2, nameout);if nargin==0,   disp(' diff_avr(name1, name2, nameout); ');   return;end;disp(name1);[data1, latencies] = read_avr(name1);disp(name2);data2 = read_avr(name2);disp('Subracting 2 from 1');diff = data1-data2;%disp('Average referencing');%diff = avg_ref(diff);disp(nameout);if length(latencies)>1, interval=latencies(2)-latencies(1); else, interval=1; end;write_avr(diff, nameout, latencies(1), interval);