[filename pathname] = uigetfile('*.elp', 'Choose file with spherical electrode positions!');name = sprintf('%s%s', pathname, filename); fid=fopen(name, 'r');fgetl(fid);    % Headerfgetl(fid);fgetl(fid);cnt = 1;line = fgetl(fid);while line ~= -1,	[value, rest] = strtok(line);	indata(1,cnt) = str2num(value);	[value] = strtok(rest);	indata(2,cnt) = str2num(value);	line = fgetl(fid);	cnt = cnt + 1;end;fclose(fid);outdata = elp2coor(indata);[x,namelength] = size(name);name=name(1:namelength-4)name=strcat(name,'.dat');fid = fopen(name, 'w');for i=1:cnt-1,   fprintf(fid, '%g %g %g\n', outdata(1,i),outdata(2,i),outdata(3,i));end;fclose(fid);