function exportallzavwav(whatsubject)load (['/export/jesse/data3/ZAV/longmndata/' whatsubject 'LANI_mn2050.wa.mat']);correctedanimat = bslcorrWAMat(AvgWaPower,25:175);disp ('animal baseline-corrected.');load (['/export/jesse/data3/ZAV/longmndata/' whatsubject 'LFAC_mn2050.wa.mat']);correctedfacmat = bslcorrWAMat(AvgWaPower,25:175);disp ('face baseline-corrected.');load (['/export/jesse/data3/ZAV/longmndata/' whatsubject 'LARM_mn2050.wa.mat']);correctedarmmat = bslcorrWAMat(AvgWaPower,25:175);disp ('arm baseline-corrected.');load (['/export/jesse/data3/ZAV/longmndata/' whatsubject 'LLEG_mn2050.wa.mat']);correctedlegmat = bslcorrWAMat(AvgWaPower,25:175);disp ('leg baseline-corrected.');clear AvgWaPower;atfwcmf(correctedanimat, correctedfacmat, correctedarmmat, correctedlegmat);