function [I,Y] = find_nearest(grid, locations);if nargin==0,   disp(' [I,Y] = find_nearest(grid, locations); ');   return;end;nr_locs = length(locations(1,:));nr_grids = length(grid(1,:));% Finde Gridorte nahe den angegebenen Positionen clear diff;for j=1:nr_locs, 		diff(1,1:nr_grids) = grid(1,1:nr_grids) - locations(1,j);         diff(2,1:nr_grids) = grid(2,1:nr_grids) - locations(2,j);      diff(3,1:nr_grids) = grid(3,1:nr_grids) - locations(3,j);     	diffnorm = norm_col(diff);  		[Y(j),I(j)] = min(diffnorm);end;