function [pairs] = find_pairs(matrix, comp, tol);if nargin==0,   disp(' [pairs] = find_pairs(matrix, comp, tol); ');   return;end;if isempty(comp),   comp = 1;end;if isempty(tol),   tol = 0.001;end;[m n] = size(matrix);count = 1;for i=1:length(matrix(1,:));   for j=1:m,	   if j==comp,   	   diff(j,:) = abs(matrix(comp,:) + matrix(comp,i));      else,   	   diff(j,:) = abs(matrix(j,:) - matrix(j,i));   	end;   end;   diffnorm = norm_col(diff);   [y, k] = min(diffnorm);   if (y<tol) & (abs(matrix(comp,i))>tol),      if matrix(comp,i)<0,	      pairs(1,count) = i;         pairs(2,count) = k;      else,         pairs(1,count) = k;         pairs(2,count) = i;      end;      matrix(1:m,k) = 0;      count = count+1;   end;end;