% Given a leadfield matrix, a vector of regularization parameters, and a data vector% the residual variance of the pseudosolution for every regul. param. is computed an plotted% OH 09.12.97% uses res_var(), pinv_tikh()function [resvar] = find_regpar(lfdmat, regpars, data);if nargin==0,   disp(' [resvar] = find_regpar(lfdmat, regpars, data); ');   return;end; for i=1:length(regpars),   disp(regpars(i));	disp('Computing pseudoinverse (G)');   G = pinv_tikh(lfdmat, regpars(i));   inv = G*data;   resvar(i) = res_var(lfdmat, inv, data);end;plot(regpars, resvar, 'x'); hold on;plot(regpars, resvar);xlabel('Regularisierungsparameter');ylabel('Restvarianz');