function focmeanval = focmeangridcell(inmeangridmat, chnls)% compute mean of a focus defined by chnls, if chnls empty take upper hemisphere (= gfp)if isempty(chnls),   % compute global average   noofchnls = 121;   chnls = 1:121;else   noofchnls = size(chnls,2);endfocmeanval = 0;for i=1:noofchnls,   %disp(chnls(i));   focmeanval = focmeanval + squeeze(inmeangridmat(chnls(i)));endfocmeanval = focmeanval / noofchnls;