% generates morlet wavelets with given frequency range etc.function [wavelet] = gener_wav_time(NPoints, delta_f0, f0_start, f0_end);plotyon= 'n';m=5;index = 1;for f0 = f0_start : delta_f0 : f0_end    %   ueber  Peakfrequenz der Wavelets : index f0 bis nyquist		sigma_f = f0 / m        sigma_t = 1/(2*pi*sigma_f) .*100		A = 1/sqrt(sigma_t * sqrt(pi))   				 for t = 1:1:NPoints  ;		 wavelet(index, t) = Agauss_time(t, sigma_t, A, f0);		 end	  	index = index+1;%	f0   end  if plotyon == 'y'	figure	for j = 1 : f0     			plot(wavelet(j, :));		 hold on	endhold off	end