function [grand, paths, names] = grand_mean(paths, names, format, from, to, nameout);if nargin==0,   disp(' [grand, paths, names] = grand_mean(paths, names, format, from, to, nameout); ');   return;end;if isempty(names),   if strcmp(format, 'avr'), ending = '*.avr'; end;	if strcmp(format, 'mfxtxt'), ending = '*.asc'; end;   [namelist, names, paths] = read_filelist(ending);   nr_names = length(namelist(:,1));else   nr_names = length(names(:,1));   for i=1:nr_names,      namelist(i,1:length(names(i,:))+length(paths(i,:))) = sprintf('%s%s', paths(i,:), names(i,:));   end;end;namelist[data, m, n, latencies] = read_data(namelist, '', format);if isempty(from), from=1; end;if isempty(to), to=n(1); end;grand = zeros(size(data(:,:,1)));for i=1:nr_names,   grand = grand + data(:,:,i);end;grand = grand/nr_names;if n(1)>1,   step = latencies(1,2)-latencies(1,1);else,   step = 1;end;disp(nameout);if ~isempty(nameout),   write_avr(grand, nameout, latencies(1,1), step);end;