function [diploc, lfdmat, data, G, invsol] = inv_pb(lfdmat);disp('Reading dipole locations (diploc)');diploc = read_matrix(1384, 3, 'MadMac:matlab:data:aphasie:elp:diploc_sph_08_02_01_-06.dat');if nargin==0,	disp('Reading leadfield matrix (lfdmat)');	lfdmat = read_matrix(4152, 65, 'MadMac:matlab:data:pb:leadfields:lead65_sph_08_02_01_-06.dat');	lfdmat = lfdmat';end;disp('Reading data (data)');data = read_matrix(65, 91,  'MadMac:matlab:data:pb:pb_audi:faln.asc');disp('Computing pseudoinverse (G)');G = pinv_tikh(lfdmat, 100.0);disp('Computing inverse solutions (invsol)');invsol = inv_recon(G, data(:,15:20), 3);