function n=normalize(m,squared)% Normalizes the columns of m by the norm of the columns of m% if nargin==2 the square of the norm of the columns is used% it does not matter what value is entered in square% OH 18.12.97[f,c]=size(m);norms=zeros(c,1);norms=diagprod(m',m);if nargin==1,  norms=sqrt(norms);end;for i=1:length(m(1,:)),   if norms(i)~=0,      n(:,i) = m(:,i)/norms(i);   else,      n(:,i) = zeros(length(m(:,1)),1);   end;end;%over