% Liest Liste von separaten Bitmaps (format = 'bmp') ein, fügt diese in rechteckiger% Anordnung zu einem Bitmap zusammen% Nützlich etwa wenn viele Bilder eines Experiments übersichtlich auf einer Seite% dargestellt werden sollenfunction [nr_pixels, paths, names] = pixel_frequency(paths, names, format, weights);if nargin==0,   disp(' [nr_pixels, paths, names] = pixel_frequency(paths, names, format, weights); ');   return;end;if isempty(names),							% Ggfls. interaktive Erstellung der Fileliste  	ending = sprintf('*.%s', format);   [namelist, names, paths] = read_filelist(ending);   nr_names = length(namelist(:,1));else   nr_names = length(names(:,1));   for i=1:nr_names,      namelist(i,1:length(deblank(names(i,:)))+length(deblank(paths(i,:)))) = sprintf('%s%s', deblank(paths(i,:)), deblank(names(i,:)));   end;end;for i=1:nr_names,						% Einlesen der separaten Bitmaps   [tmpmat, tmpmap] = imread(namelist(i,:));   [m(i) n(i)] = size(tmpmat);   [mm(i) nm(i)] = size(tmpmap);   [picmat(1:m(i),1:n(i),i), map(1:mm(i),1:nm(i),i)] = imread(namelist(i,:));end;nr_pixels = 0;for i=1:nr_names,   tmpvec = reshape(picmat(:,:,i), 1, m(i)*n(i));   nr_pixels = nr_pixels+length(find(tmpvec))*weights(i);end;