function [data, normcol, maxcol, paths, names] = plot_norm_avr(paths, names, format, elplist, from, to, normflag, nameout);if nargin==0,   disp(' [data, normcol, maxcol, paths, names] = plot_norm_avr(paths, names, format, elplist, from, to, normflag, nameout); ');   return;end;if isempty(names),   if strcmp(format, 'avr')|strcmp(format, 'avrnoref'), ending = '*.avr'; end;	if strcmp(format, 'mfxtxt'), ending = '*.asc'; end;   [namelist, names, paths] = read_filelist(ending);   nr_names = length(namelist(:,1));else   nr_names = length(names(:,1));   for i=1:nr_names,      namelist(i,1:length(deblank(names(i,:)))+length(deblank(paths(i,:)))) = sprintf('%s%s', deblank(paths(i,:)), deblank(names(i,:)));   end;end;namelist[data, m, n, latencies] = read_data(namelist, elplist, format);nr_names = length(namelist(:,1));if isempty(from), from=1; end;if isempty(to), to=n(1); end;colours = char('r', 'b', 'g', 'm', 'c', 'y', 'b', 'w');if nr_names>length(colours),	disp('Not enough colours for all files! (cnv_pca)');end;for i=1:nr_names,	normcol(:,i) = norm_col(data(1:m(i),1:n(i),i));	maxcol(:,i) = max(abs(data(1:m(i),1:n(i),i)))';	if strcmp(normflag,'normmax'),     	normcol(:,i) = normcol(:,i)/max(normcol(:,i));      maxcol(:,i) = maxcol(:,i)/max(maxcol(:,i));   end;end;figure;set(gcf,'DefaultTextInterpreter','none');clf;for i=1:nr_names,   if i<length(colours), c = colours(i); else, c = 'b'; end;   plot(normcol(:,i), c); hold on;end;legend(names);title('Norms of columns');figure;set(gcf,'DefaultTextInterpreter','none');clf;for i=1:nr_names,   if i<length(colours), c = colours(i); else, c = 'b'; end;   plot(maxcol(:,i), c); hold on;end;legend(names);title('Maxima of absolute values of columns');