function plotband(inmat, nmf1,nmf2, chnls, clrs)frequencystep = 2;firstfreq = 8; % in Hzf1 = 1+(nmf1-firstfreq)/frequencystep;f2 = 1+(nmf2-firstfreq)/frequencystep;t1 = 26; %from (samplepoints)t2 = 462; %to (samplepoints) bandmat = meanfreq(inmat, f1,f2);if isempty(chnls),   % compute global average   noofchnls = 121;   chnls = 1:121;else   noofchnls = size(chnls,2);enddisp('noofchnls');disp(noofchnls);disp('inmat');disp(size(inmat,3));disp('plotmat');plotmat = zeros(1,size(inmat,3));disp(size(plotmat));disp('bandmat');disp(size(bandmat));for i=1:noofchnls,   %disp(chnls(i));   plotmat = plotmat+squeeze(bandmat(chnls(i),:));endplotmat = plotmat / noofchnls;plot(plotmat(t1:t2),'color',clrs);%set(gca,'YTickmode','manual');%set(gca,'YTick',[1:4:(f2-f1)]);%set(gca,'YTickLabel',[nmf1:8:nmf2]);set(gca,'XTickMode','manual');set(gca,'Xtick',[1:75:956]);set(gca,'XTickLabel',[-300:150:656]);