function plotbandz(facmat,armmat,legmat, f1, f2)% left hemi leg, arm, facsubplot(2,3,1)% chnls 3,4,10,11,12,13holdplotband(facmat, f1,f2,[3,4,10,11,12,13], [0 0 1] );plotband(armmat, f1,f2,[3,4,10,11,12,13], [1 0 0] );plotband(legmat, f1,f2,[3,4,10,11,12,13], [0 1 0] );line([0 462],[0 0], 'color', [0 0 0]);subplot(2,3,2)% chnls 23,24,25,41,42,43holdplotband(facmat, f1,f2,[23,24,25,41,42,43], [0 0 1] );plotband(armmat, f1,f2,[23,24,25,41,42,43], [1 0 0] );plotband(legmat, f1,f2,[23,24,25,41,42,43], [0 1 0] );line([0 462],[0 0], 'color', [0 0 0]);subplot(2,3,3)% chnls 62,63,64,85,86,87holdplotband(facmat, f1,f2,[62,63,64,85,86,87], [0 0 1] );plotband(armmat, f1,f2,[62,63,64,85,86,87], [1 0 0] );plotband(legmat, f1,f2,[62,63,64,85,86,87], [0 1 0] );line([0 462],[0 0], 'color', [0 0 0]);% right hemi, leg, arm, facsubplot(2,3,4)%chnls 6,7,15,16,17,18holdplotband(facmat, f1,f2,[6,7,15,16,17,18], [0 0 1] );plotband(armmat, f1,f2,[6,7,15,16,17,18], [1 0 0] );plotband(legmat, f1,f2,[6,7,15,16,17,18], [0 1 0] );line([0 462],[0 0], 'color', [0 0 0]);subplot(2,3,5)% chnls 32,33,34,52,53,54holdplotband(facmat, f1,f2,[32,33,34,52,53,54], [0 0 1] );plotband(armmat, f1,f2,[32,33,34,52,53,54], [1 0 0] );plotband(legmat, f1,f2,[32,33,34,52,53,54], [0 1 0] );line([0 462],[0 0], 'color', [0 0 0]);subplot(2,3,6)% chnls 75,76,77,100,101,102holdplotband(facmat, f1,f2,[75,76,77,100,101,102], [0 0 1] );plotband(armmat, f1,f2,[75,76,77,100,101,102], [1 0 0] );plotband(legmat, f1,f2,[75,76,77,100,101,102], [0 1 0] );line([0 462],[0 0], 'color', [0 0 0]);