function result = plotmatz(inmat, nmf1, nmf2, caxmin, caxmax, Nsteps)axeshandle = axes;f1 = 1+(nmf1-8)/2;f2 = 1+(nmf2-8)/2;t1 = 26; %from (samplepoints)t2 = 462; %to (samplepoints) subplot(2,3,1)% chnls 3,4,10,11,12,13contourf(squeeze((inmat(3,f1:f2,t1:t2)+inmat(4,f1:f2,t1:t2)+inmat(10,f1:f2,t1:t2)+inmat(11,f1:f2,t1:t2)+inmat(12,f1:f2,t1:t2)+inmat(13,f1:f2,t1:t2)/6)),Nsteps)%surf(squeeze((inmat(3,f1:f2,:)+inmat(4,f1:f2,:)+inmat(10,f1:f2,:)+inmat(11,f1:f2,:)+inmat(12,f1:f2,:)+inmat(13,f1:f2,:)/6)))%shading interpcaxis([caxmin,caxmax])%set(gca,'Ylim',[0 listtest_yminmax]);    set(gca,'YTickmode','manual');set(gca,'YTick',[1:4:(f2-f1)]);set(gca,'YTickLabel',[nmf1:8:nmf2]);set(gca,'XTickMode','manual');set(gca,'Xtick',[1:75:956]);set(gca,'XTickLabel',[-300:150:656]);title('LEFT leg')%colorbarsubplot(2,3,2)% chnls 23,24,25,41,42,43contourf(squeeze((inmat(23,f1:f2,t1:t2)+inmat(24,f1:f2,t1:t2)+inmat(25,f1:f2,t1:t2)+inmat(41,f1:f2,t1:t2)+inmat(42,f1:f2,t1:t2)+inmat(43,f1:f2,t1:t2)/6)),Nsteps)%surf(squeeze((inmat(23,f1:f2,:)+inmat(24,f1:f2,:)+inmat(25,f1:f2,:)+inmat(41,f1:f2,:)+inmat(42,f1:f2,:)+inmat(43,f1:f2,:)/6)))%shading interpcaxis([caxmin,caxmax])set(gca,'YTickmode','manual');set(gca,'YTick',[1:4:(f2-f1)]);set(gca,'YTickLabel',[nmf1:8:nmf2]);set(gca,'XTickMode','manual');set(gca,'Xtick',[1:75:956]);set(gca,'XTickLabel',[-300:150:656]);title('arm')%colorbarsubplot(2,3,3)% chnls 62,63,64,85,86,87contourf(squeeze((inmat(62,f1:f2,t1:t2)+inmat(63,f1:f2,t1:t2)+inmat(64,f1:f2,t1:t2)+inmat(85,f1:f2,t1:t2)+inmat(86,f1:f2,t1:t2)+inmat(87,f1:f2,t1:t2)/6)),Nsteps)%surf(squeeze((inmat(62,f1:f2,:)+inmat(63,f1:f2,:)+inmat(64,f1:f2,:)+inmat(85,f1:f2,:)+inmat(86,f1:f2,:)+inmat(87,f1:f2,:)/6)))%shading interpcaxis([caxmin,caxmax])set(gca,'YTickmode','manual');set(gca,'YTick',[1:4:(f2-f1)]);set(gca,'YTickLabel',[nmf1:8:nmf2]);set(gca,'XTickMode','manual');set(gca,'Xtick',[1:75:956]);set(gca,'XTickLabel',[-300:150:656]);title('face')%colorbarsubplot(2,3,4)%chnls 6,7,15,16,17,18contourf(squeeze((inmat(6,f1:f2,t1:t2)+inmat(7,f1:f2,t1:t2)+inmat(15,f1:f2,t1:t2)+inmat(16,f1:f2,t1:t2)+inmat(17,f1:f2,t1:t2)+inmat(18,f1:f2,t1:t2)/6)),Nsteps)%surf(squeeze((inmat(6,f1:f2,:)+inmat(7,f1:f2,:)+inmat(15,f1:f2,:)+inmat(16,f1:f2,:)+inmat(17,f1:f2,:)+inmat(18,f1:f2,:)/6)))%shading interpcaxis([caxmin,caxmax])set(gca,'YTickmode','manual');set(gca,'YTick',[1:4:(f2-f1)]);set(gca,'YTickLabel',[nmf1:8:nmf2]);set(gca,'XTickMode','manual');set(gca,'Xtick',[1:75:956]);set(gca,'XTickLabel',[-300:150:656]);title('RIGHT leg')%colorbarsubplot(2,3,5)% chnls 32,33,34,52,53,54contourf(squeeze((inmat(32,f1:f2,t1:t2)+inmat(33,f1:f2,t1:t2)+inmat(34,f1:f2,t1:t2)+inmat(52,f1:f2,t1:t2)+inmat(53,f1:f2,t1:t2)+inmat(54,f1:f2,t1:t2)/6)),Nsteps)%surf(squeeze((inmat(32,f1:f2,:)+inmat(33,f1:f2,:)+inmat(34,f1:f2,:)+inmat(52,f1:f2,:)+inmat(53,f1:f2,:)+inmat(54,f1:f2,:)/6)))%shading interpcaxis([caxmin,caxmax])set(gca,'YTickmode','manual');set(gca,'YTick',[1:4:(f2-f1)]);set(gca,'YTickLabel',[nmf1:8:nmf2]);set(gca,'XTickMode','manual');set(gca,'Xtick',[1:75:956]);set(gca,'XTickLabel',[-300:150:656]);title('arm')%colorbarsubplot(2,3,6)% chnls 75,76,77,100,101,102contourf(squeeze((inmat(75,f1:f2,t1:t2)+inmat(76,f1:f2,t1:t2)+inmat(77,f1:f2,t1:t2)+inmat(100,f1:f2,t1:t2)+inmat(101,f1:f2,t1:t2)+inmat(102,f1:f2,t1:t2)/6)),Nsteps)%surf(squeeze((inmat(75,f1:f2,:)+inmat(76,f1:f2,:)+inmat(77,f1:f2,:)+inmat(100,f1:f2,:)+inmat(101,f1:f2,:)+inmat(102,f1:f2,:)/6)))%shading interpcaxis([caxmin,caxmax])set(gca,'YTickmode','manual');set(gca,'YTick',[1:4:(f2-f1)]);set(gca,'YTickLabel',[nmf1:8:nmf2]);set(gca,'XTickMode','manual');set(gca,'Xtick',[1:75:956]);set(gca,'XTickLabel',[-300:150:656]);title('face')%colorbar (axeshandle);%shading interpresult = 0;%end