% Read polar coordinates (theta/phi) from *.elp-file% First three lines are ignored, first column theta, second column phi assumed% OH 22.10.97function [pol_coor] = read_elp(name);if nargin==0,	[filename pathname] = uigetfile('*.elp', 'Choose file with spherical electrode positions!');	name = sprintf('%s%s', pathname, filename);end; fid=fopen(name, 'r');fgetl(fid);    % Headerfgetl(fid);fgetl(fid);cnt = 1;line = fgetl(fid);while line ~= -1,	[value, rest] = strtok(line);	pol_coor(1,cnt) = str2num(value);	[value] = strtok(rest);	pol_coor(2,cnt) = str2num(value);	line = fgetl(fid);	cnt = cnt + 1;end;fclose(fid);