% Read list of files in array "namelist" (pathnames included), filenames% and pathnames separately in arrays "names", "paths"% "extension" (optional): Filter for files to be displayed% OH 07.02.98function [namelist, names, paths] = read_filelist(extension);if nargin==0 | isempty(extension),   extension = '*.*';end;name = 'allfiles.txt';path = '';i=1;while name~=0,   if isempty(path),      filter = extension;   else,      filter = sprintf('%s%s', path, extension);   end;   [name, path] = uigetfile(filter, 'Choose a file!');   if name~=0,	   names(i,1:length(name)) = name;   	paths(i,1:length(path)) = path;      namelist(i,1:length(name)+length(path)) = sprintf('%s%s', paths(i,:), names(i,:));      disp(namelist(i,:));      i = i+1;   end;end;