function [sensors, name, pathname, filename] = read_mfxpos(filename, factor);if nargin==0,   disp(' [sensors, name, pathname, filename] = read_mfxpos(filename, factor); ');   return;end;if nargin==1 | isempty(factor),   factor = 1;end;if isempty(filename),		% No name specified	[name, pathname] = uigetfile('*.chn', 'Choose a MFX-position file!');	filename = sprintf('%s%s', pathname, name);end;header_lines = 6;fid = fopen(filename, 'r');for i=1:header_lines,   line = fgetl(fid);end;line = fgetl(fid);i=1;while isstr(line),   [text, R] = strtok(line, ':')   [points, values] = strtok(R, ' ')   sensors(1:3,i) = sscanf(values, '%f', 3);   sensors(1:3,i) = factor*sensors(1:3,i);   line = fgetl(fid);   [text, R] = strtok(line, ':')   [points, values] = strtok(R, ' ')   sensors(4:6,i) = sscanf(values, '%f', 3);	line = fgetl(fid);	i = i+1;end;   