% Compute signal to noise ratio as the square root of the ratio of mean power of the signal and the% mean power of the baseline% OH 05.12.97, 09.12.97% uses norm_col()function [msn, mampl, mstd, snv, mamplv, stdv] = signal2noise(data, nr_baseelem, signal_from, signal_to);if nargin==0,   disp(' [msn, mampl, mstd, snv, mamplv, stdv] = signal2noise(data, nr_baseelem, signal_from, signal_to); ');   return;end;if nargin==2,			% No signal range specified   signal_from = nr_baseelem+1;   signal_to = length(data(1,:));end;stdv = std(data(:,1:nr_baseelem)', 1);		% Vector of standard deviations for the baseline intervallmamplv = mean( abs(data(:,signal_from:signal_to))' );		% Vector of mean absolute amplitudes of signalmampl = mean(mamplv);				% Mean absolute signal amplitudesnv = mamplv./stdv;		% Vector of mean signal to noise ratios (absolute amplitudes / std. dev.)mstd = mean(stdv);			% Mean standard deviation of baselinemsn = mean(snv);				% Mean signal to noise ratio