function G = trop(lfdmat, gridloc, solloc, dim, width, lambda);nr_gridpoints = length(gridloc(1,:));nr_solpoints = length(solloc(1,:));gram = lfdmat*lfdmat';disp('Computing forward solutions');for i=1:nr_solpoints,   if mod(i,10)==0, disp(i); end;   target = trop_target_gauss(gridloc, solloc(:,i), dim, width);   forward(:,(i-1)*dim+1:i*dim) = lfdmat*target;end;disp('Computing gereralized inverse');G = (gram+lambda*eye(size(gram)))\forward;