% extracts paramters from 3d wa result matrixfunction[GMpower] = wa3d2_avg(con);vps = ['02';'05';'07';'09';'10';'11';'14';'15';'16';'17'];for vpindex = 1 : size(vps,1)   vp = vps(vpindex,:)   file = [ 'vp' vp '.E1.app' con '.wa.mat'];   path = ['F:\emolat\'];   eval(['load ' path file]);   if vpindex == 1      sumAvgPower = AvgWaPower;   else      sumAvgPower = sumAvgPower + AvgWaPower;   end         clear AvgWaPower   %figure(vpindex)   %size(corrWaMat);   % caution : freqaxis and taxis for plots only   %freqaxis = [14.64:2.44:61];   %taxis=(-1796:4:1000);   %contourf(taxis,freqaxis, squeeze(corrWaMat(1, [3:22],[51:750] )), 18);          %	title([' vp ' vps(vpindex,:)]);         %	colorbar                       %if vpindex  == 1           %sumMat = squeeze(corrWaMat(1, [3:22],[51:750] ));           %else           %sumMat = sumMat+squeeze(corrWaMat(1, [3:22],[51:750] ));           %end                      %resgammavec(vpindex) = mean(mean(squeeze(corrWaMat(1, [freqbandvec],[timepointvec]))));           %clear corrWaMatendGMpower = sumAvgPower ./size(vps, 1);eval(['save ' path 'GM.E1.app' con '.wa.mat GMpower']);