% wa3d2parabatch% calls wa3d2para for conditionsfunction [] = wa3d2parabatch(timevec, freqvec);condvec = ['OK'; 'EX'; 'SP'];%timevec = [545:600];%freqvec = [10:15];  vps = ['03';'05';'06';'08';'11';'12';'13';'15';'17';'18';'19'];%parmat = zeros(10,13,5);%parmatEX = zeros(10,5,13);%parmatSP = zeros(10,5,13);sensorvec = [1:129]; parmat = zeros(length(vps(:, 1)),length(sensorvec) );	for cond = 1 : 3          	for vpindex = 1 : size(vps,1)                   vp = vps(vpindex,:);		   file = [ 'vp' num2str(vp) condvec(cond, :) '.E1.appg'  '.wa.mat'];		   path = ['F:\operant_neu\'];   		eval(['load ' path file]);         bslcorrMat = bslcorrwamat(AvgWaPower, [50:250]);                  	      					for freqindex = 1 : length(freqvec(:,1))                    parmat(vpindex, :)  = wa3d2para_elcs(bslcorrMat, sensorvec, timevec, freqvec);       				         			end %freq      	   			         end %vps               eval(['save 'path 'meanspecPOW' condvec(cond, :) '_' num2str(timevec(1)) '_'num2str(timevec(length(timevec))) '_'num2str(freqvec(1)) '_'num2str(freqvec(length(freqvec))) ' parmat -ascii'])               end % conditions      