function [topomat] = wa3d2topos(inMat, out_name, freqvec, timevec)% 3d2topos% computes topo vectors from 3d wa matrix%timevec = [1:512];%freqvec = [7:16];%for t = 1:length(timevec)   for f = 1 : length(freqvec(:, 1)) % aendern, wenn mehrere freqs berechnet werden sollen            %	topomat = squeeze(mean(inMat(:, freqvec(f), [timevec]), 2));	% fuer mehrere einzelne frequenzen     	  topomat = squeeze(mean(inMat(:, freqvec, [timevec]), 2)); 		% fuer gemittelte frequenzen                  size(topomat)      write_avr(topomat, [out_name num2str(freqvec(f)) '.avr'] , -100, 2)      end%end      