function [x, r] = when_changes_radius(xyz, tol);if nargin==1,	tol = 0;end;cnt = 1;for i=1:length(xyz)-1,	radius = norm(xyz(:,i));	if abs(radius-norm(xyz(:,i+1)))>tol,      x(cnt) = i;      r(cnt) = radius;		cnt = cnt+1;		outtxt = sprintf('%f   %d', radius, i);		disp(outtxt);	end;end;x(cnt) = length(xyz);r(cnt) = norm(xyz(:,i+1));outtxt = sprintf('%f   %d', norm(xyz(:,i+1)), i+1);disp(outtxt);